    {        $id = $this->input->post('id');        $name = $this->input->post('name');//        var_dump($id,$name);//        header('Content-Type: application/json; charset=utf-8');//        echo json_encode($name);        if($id > 0)        {            $query = query("UPDATE `tbl_ministry` SET `name_ministry` = '{$name}' WHERE `tbl_ministry`.`id` = '{$id}'");        }        header('Content-Type: application/json; charset=utf-8');        echo json_encode(['status' => 'success']);    }}