k(function () {            if ($("#content_details").hasClass('drop_menu'))            {                $("#content_details").addClass('drop_menu1').removeClass('drop_menu');            } else {                $("#content_details").addClass('drop_menu').removeClass('drop_menu1');            }        });//search box js//        $("#flip").click(function () {            $("#panel").slideToggle("5000");        });// sticky js//        $(window).scroll(function () {            if ($(window).scrollTop() >= 500) {                $('nav').addClass('stick');            } else {                $('nav').removeClass('stick');            }        });    });</script><!-- ======================================== --><!-- /*<style media="screen">@import url("http://netdna.bootstrapcdn.com/font-awesome/4.0.3/css/font-awesome.min.css");body { margin-top:20px; }.fa { font-size: 50px;text-align: right;position: absolute;top: 7px;right: 27px;outline: none; }a { transition: all .3s ease;-webkit-transition: all .3s ease;-moz-transition: all .3s ease;-o-transition: all .3s ease; }/* Visitor */a.visitor i,.visitor h4.list-group-item-heading { color:#E48A07;}a.visitor:hover { background-color:#E48A07; }a.visitor:hover * { color:#FFF; }/* Facebook */a.facebook-like i,.facebook-like h4.list-group-item-heading { color:#3b5998; }a.facebook-like:hover { background-color:#3b5998; }a.facebook-like:hover * { color:#FFF; }/* Google */a.google-plus i,.google-plus h4.list-group-item-heading { color:#dd4b39; }a.google-plus:hover { background-color:#dd4b39; }a.google-plus:hover * { color:#FFF; }/* Twitter */a.twitter i,.twitter h4.list-group-item-heading { color:#00acee; }a.twitter:hover { background-color:#00acee; }a.twitter:hover * { color:#FFF; }/* Linkedin */a.linkedin i,.linkedin h4.list-group-item-heading { color:#0e76a8; }a.linkedin:hover { background-color:#0e76a8; }