t/javascript">        $(function () {            $body = $("body");            $(document).bind("ajaxStart", function () {                $body.addClass("loading");                console.log('ajaxStart');            }).bind("ajaxSend", function () {                $body.addClass("loading");                console.log('ajaxSend');            }).bind("ajaxStop", function () {                $body.removeClass("loading");                console.log('ajaxStop');            }).bind("ajaxSuccess", function () {                $body.removeClass("loading");                console.log('ajaxSuccess');            }).bind("ajaxComplete", function () {                $body.removeClass("loading");                console.log('ajaxComplete');            }).bind("ajaxError", function () {                $body.removeClass("loading");                console.log('ajaxError');            });        });    </script>    <script type="text/javascript">        function simple_encrypt(encryptedText) { // encryptedText            var str = '';            $.ajax({                url: "<?= site_url('admin/users/encrypt') ?>",                dataType: "html",                async: false,                data: {encryptedText: encryptedText},                type: "post",                success: function (data) {                    str = data.trim();                },                error: function (XMLHttpRequest, textStatus, errorThrown) {                    alert(textStatus);                }            });            return str;        }        // paste ===================        /* $(function() {         $('form').attr('autocomplete', 'off');         // =======================         var controls = $('form');         controls.bind("paste", function() {         return false;         });         controls.bind("drop", function() {         return false;         });         controls.bind("cut", function() {         return false;         });         controls.bind("copy", function() {         return false;         });         }); */        $(function () {            $('form').attr('autocomplete', 'off');            // allow only number ====================            $('body').delegate('[number]', 'keypress', function (e) {                if (e.which != 8 && e.which != 0 && (e.which < 48 || e.which > 57)) {                    return false;                }            });            // close keyboard ====================            $('body').delegate('[closekey]', 'keypress', function (e) {                if (e.which != 8 && e.which != 0 && (e.which < 1000)) {                    return false;                }            });            // allow only datetime===========            $('body').delegate('[date]', 'keypress', function (e) {                $(this).attr('maxlength', '10');                var code = (e.keyCode || e.which);//                        alert(code);                if (code > 47 && code < 58 || code == 45) {                    return;                } else {                    return false;                }            });            // allow only gender===========            $('body').delegate('[gender]', 'keyup', function (e) {                var code = (e.keyCode || e.which);                if (code == 9 || code == 8 || code == 16 || code == 27 || code == 32 || code == 35 || code == 36 || code == 37 || code == 38 || code == 39 || code == 40 || code == 46) {                    return;                } else {                    var node = $(this).attr('maxlength', '5');                    node.val(node.val().replace(/[a-z A-Z ក-ន ផ-យ ល-វ ហ-អ ា-ិ ឹ-ឹ ូ-ោះ ់ ៉ ឥ-ឪ ឱ ឰ ឳ ឭ ឮ;''"""0-9/*+-|}!#$%&()]/g, ''));                }            });            // allow only decimal ====================            $('body').delegate('[decimal]', 'keydown', function (e) {                var key = e.charCode || e.keyCode || 0;                // allow backspace, tab, delete, arrows, numbers and keypad numbers ONLY                // home, end, period, and numpad decimal                if (key == 110 || key == 190) {                    var text = $(this).val();                    if (text.toString().indexOf(".") != -1) {                        return false;                    } else if (text.toString().indexOf(",") != -1) {                        return false;                    }                }                return (key == 13 ||                        key == 8 ||                        key == 9 ||                        key == 46 ||                        key == 110 ||                        key == 190 ||                        (key >= 35 && key <= 40) ||                        (key >= 48 && key <= 57) ||                        (key >= 96 && key <= 105)                        );            });            $('form').bind('submit', function () {                return ModelStateIsValid();            });        }); // end ready fun. ======================        // checked validate form submit ============        function ModelStateIsValid() {            var IsValid = true;            $('[validate_act],[notnull],[compulsory]').each(function (i) {                $(this).removeClass('validate_act');                if ($.trim($(this).val()) == "") {                    IsValid = false;                    alert("ទិន្នន័យមិនគ្រប់គ្រាន់ !");                    $(this).addClass('validate_act');                    $(this).focus();                    return false;                }            });            $('[gtzero]').each(function (i) {                if ($.trim($(this).val()) - 0 <= 0) {                    IsValid = false;                    alert("Pls fill all number of amount > 0 !!");                    return false;                }            });            return IsValid;        }    </script></head><body>    <div class="xmodal"></div>